# PROCESS FILE
line_break = 1
header = 0
delete = 0
next_page = ''
pagebreak = ''

# MAIN RUN-THROUGH of LINES
with open(sys.argv[1] + '.txt', 'r') as read_file:
    for line in read_file:
        line_break = line_break + 1
        # Only read odd-numbered lines
        if line_break % 2 == 0:
            # Find all <note> tags or [NOTE: ], xml tags and replace spaces with underscores
            for note in re.findall(r'<note>([^<]*)</note>', line):
                line = re.sub(r"(.*)%s(.*)" % re.escape(note), r"\1%s\2" % note.replace(' ', '_'), line)
            for shorthandNote in re.findall(r'\[NOTE:([^\]]*)\]', line):
                line = re.sub(r'(.*)\[%s\](.*)' % re.escape(shorthandNote), r'\1<note>%s</note>\2' % shorthandNote.replace(' ', '_'), line)
            for xmlTag in re.findall(r'<([^>]*)>', line):
                line = re.sub(r'(.*)<%s>(.*)' % re.escape(xmlTag), r'\1<%s>\2' % xmlTag.replace(' ', '_'), line)
            # Check for additions (single word)
            for addit in re.findall(r"<add([^>]*)>", line):
                line = re.sub(r'(.*)%s(.*)' % re.escape(addit), r'\1%s\2' % addit.replace(' ', '_'), line)
            # Check for multi-word additions
            for multiAdd in re.findall(r'<add(.*[\s].*)</add>', line):
                addBeforeMatch = multiAdd[0:re.match('">', multiAdd)]
                addAfterMatch = "</add>"
                addBefore = "+<add" + multiAdd[0:re.match('">', multiAdd)]
                line = re.sub(r'(.*)<add%s(.*)%s(.*)' % (re.escape(addBeforeMatch), re.escape(addAfterMatch)), r'\1%s\2\%s\3' % (addBefore, "</add>+"), line)
            # Process lines
            line_number = line_break / 2
            facs = line.split()
            write_file.write("<lb n=\"%s\"/>\n" % line_number)
            i = 0
            while i < len(facs):
                if facs[i][0] == "+":
                    addition = facs[i][1:facs[i].find('>') + 1]
                    write_file.write("%s<w>%s</w>" % (addition.replace('_', ' '), facs[i][facs[i].find('>') + 1:].replace('_', '')))
                elif facs[i][-1] == "+":
                    write_file.write("<w>%s</w></add>" % facs[i].replace('_', ' ').replace(r'\</add>+', ''))
                else:
                    write_file.write("<w>%s</w>" % facs[i].replace('_', ' '))

                i = i + 1
write_file.write("</body>\n</text>\n</TEI>")
